    public void Run()
    {
      var results = Input.Aggregate(
        Tuple.Create(0, 0, 1),
        (acc, chr) => chr == '(' ?
          Tuple.Create(acc.Item1 + 1, acc.Item2, acc.Item3 + 1) :
          Tuple.Create(acc.Item1 - 1, acc.Item2 == 0 && acc.Item1 == 0 ? acc.Item3 : acc.Item2, acc.Item3 + 1));

      Console.WriteLine($"Final floor: {results.Item1}");
      Console.WriteLine($"First hits basement: {results.Item2}");
      Console.ReadKey();
    }

